function self:SetNewText(text) text = text or "" self.rawText = text self:HighlightElement() end
--elementId - if empty all icons will be unhighlighted --updateAlsoBaseText - if true then also base text (with transparent icons) will be updated. function self:HighlightElement(elementId, updateOnlyIconsHighlights)
--Replacing mouse-hover interactive icons with transparent icon - to make for the top layer icons invisible. local topText = self.rawText topText = string.gsub(topText, "|NORMAL.-|e", "") topText = string.gsub(topText, "|HOVER.-|e", "") topText = string.gsub(topText, "|EMPTY.-|ID", "") topText = string.gsub(topText, "|e", "")
if not updateOnlyIconsHighlights then self:SetText(topText) end
--Replacing mouse-hover interactive icons with highlighted or unhighlighted icon.
elementId = tonumber(elementId) local lowText = self.rawText
--Showing hovered icons lowText = string.gsub(lowText, "|EMPTY.-|e", "") lowText = string.gsub(lowText, "|HOVER.-|e", function(text) local id = string.gsub(text, "|ID.-|e", "") id = string.gsub(id, "|HOVER", "") id = tonumber(id) if id and elementId and id == elementId then text = string.gsub(text, "|HOVER.-|ID", "") text = string.gsub(text, "|e", "") return text end return "" end)
--Showing normal state icons lowText = string.gsub(lowText, "|NORMAL.-|e", function(text) local id = string.gsub(text, "|ID.-|e", "") id = string.gsub(id, "|NORMAL", "") id = tonumber(id) if not (id and elementId and id == elementId) then text = string.gsub(text, "|NORMAL.-|ID", "") text = string.gsub(text, "|e", "") return text end return "" end)
self.iconsFrame:SetText(lowText) end
hooksecurefunc(self, "SetFontObject", function(frame, ...) self.iconsFrame:SetFontObject(...) end) hooksecurefunc(self, "SetFont", function(frame, ...) self.iconsFrame:SetFont(...) end) hooksecurefunc(self, "SetWidth", function(frame, ...) self.iconsFrame:SetWidth(...) end) hooksecurefunc(self, "SetHeight", function(frame, ...) self.iconsFrame:SetHeight(...) end) hooksecurefunc(self, "SetJustifyH", function(frame, ...) self.iconsFrame:SetJustifyH(...) end) hooksecurefunc(self, "SetJustifyV", function(frame, ...) self.iconsFrame:SetJustifyV(...) end) hooksecurefunc(self, "SetSpacing", function(frame, ...) self.iconsFrame:SetSpacing(...) end) hooksecurefunc(self, "Show", function(frame, ...) self.iconsFrame:Show(...) end) hooksecurefunc(self, "Hide", function(frame, ...) self.iconsFrame:Hide(...) end) hooksecurefunc(self, "SetPoint", function(frame, ...) self.iconsFrame:SetPoint(...) end)
hooksecurefunc(self, "SetFrameLevel", function(frame, ...) self.iconsFrame:SetFrameLevel(self:GetFrameLevel() - 1) end)
